// API Configuration
const API_BASE_URL = 'https://buyesi.onrender.com/api';

// API Endpoints
const ENDPOINTS = {
	BLOGS: `${API_BASE_URL}/blogs`,
	CAUSES: `${API_BASE_URL}/causes`,
	TEAM: `${API_BASE_URL}/teams`,
	GALLERY: `${API_BASE_URL}/galleries`,
	EVENTS: `${API_BASE_URL}/events`,
	CONTACT: `${API_BASE_URL}/contact`,
	VOLUNTEER: `${API_BASE_URL}/volunteers`,
};

// API Response Handler
const handleApiResponse = async (e) => {
	if (!e.ok) throw new Error(`HTTP error! status: ${e.status}`);
	return await e.json();
};

const handleResponse = async (e) => {
	if (!e.ok) throw new Error(`HTTP error! status: ${e.status}`);
	return await e.json();
};

const handleApiError = (e) => (console.error('API Error:', e), null);

// Auth API
const authAPI = {
	login: async (email, password) => {
		const response = await fetch(`${API_BASE_URL}/auth/login`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({ email, password }),
		});
		return handleResponse(response);
	},
	register: async (userData) => {
		const response = await fetch(`${API_BASE_URL}/auth/register`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(userData),
		});
		return handleResponse(response);
	},
	logout: () => {
		localStorage.removeItem('token');
		localStorage.removeItem('user');
	},
	getCurrentUser: () => {
		return JSON.parse(localStorage.getItem('user'));
	},
	isAuthenticated: () => {
		return !!localStorage.getItem('token');
	},
};

const donationAPI = {
	create: async (donationData) => {
		const response = await fetch(`${API_BASE_URL}/donations`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(donationData),
		});
		return handleResponse(response);
	},
	getAll: async () => {
		const token = localStorage.getItem('token');
		const response = await fetch(`${API_BASE_URL}/donations`, {
			method: 'GET',
			headers: { Authorization: token ? `Bearer ${token}` : '' },
		});
		return handleResponse(response);
	},
};

